import { Component } from '@angular/core';

@Component({
  selector: 'app-layout',
  templateUrl: './layout.component.html',
  styleUrls: ['./layout.component.scss']
})
export class LayoutComponent {
}
<div class="container-fluid no-gutters h-100 d-flex flex-column p-0">
    <header class="w-100 d-flex flex-shrink-1">
        <app-header></app-header>
    </header>
    <main class="w-100 d-flex flex-column flex-grow-1 py-3">
        <router-outlet></router-outlet>
    </main>
    <footer class="w-100 d-flex flex-shrink-1">
        <app-footer></app-footer>
    </footer>
</div>
